// env.js 全局
const $ = new Env("绿巨能科技");
const ckName = "ljnkj";
//-------------------- 一般不动变量区域 -------------------------------------
var Notify = 0;//0为关闭通知,1为打开通知,默认为0 //默认出签到失败提示
const notify = $.isNode() ? require('./sendNotify') : '';
let envSplitor = ["\n"]; //多账号分隔符
let userCookie = ($.isNode() ? process.env[ckName] : $.getdata(ckName)) || '';
let userList = [];
let userIdx = 0;
let userCount = 0;
// 为通知准备的空数组
$.notifyMsg = [];
//bark推送
$.barkKey = ($.isNode() ? process.env["bark_key"] : $.getdata("bark_key")) || '';
//---------------------- 自定义变量区域 -----------------------------------

//脚本入口函数main()
async function main() {
    console.log('\n============= 签到 =============\n');
    let taskall = [];
    for (let user of userList) {
        DoubleLog(`🔷账号${user.index} >> 开始签到`)
        console.log(`随机延迟${user.getRandomTime()}ms`);
        taskall.push(await user.signin());
        await $.wait(user.getRandomTime());
        taskall.push(await user.points());
        await $.wait(user.getRandomTime());
    }
    await Promise.all(taskall);
}

class UserInfo {
    constructor(str) {
        this.index = ++userIdx;
        this.token = str;
        this.body ="" 
        this.headers = { 
		    'Host': 'h5.youzan.com', 
		    'User-Agent': 'Mozilla/5.0 (Linux; Android 13; 22021211RC Build/TKQ1.220807.001; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/122.0.6261.120 Mobile Safari/537.36 XWEB/1220033 MMWEBSDK/20230504 MMWEBID/3007 MicroMessenger/8.0.37.2380(0x28002537) WeChat/arm64 Weixin NetType/WIFI Language/zh_CN ABI/arm64 MiniProgramEnv/android', 
		    'content-type': 'application/json', 
		    'extra-data': str
		  }
	}
    getRandomTime() {
        return randomInt(5000, 9000)
    }
    //签到函数
    async signin() {
        try {
            const options = {
                //签到任务调用签到接口
                url: `https://h5.youzan.com/wscump/checkin/checkinV2.json?checkinId=4534808`,
                //请求头, 所有接口通用
                headers: this.headers,
            };
            let result = await httpRequest(options,'get');
            if (result?.code == 0) {
                //obj.error是0代表完成
                DoubleLog(`✅签到成功！`);
            } else {
	            Notify = 1
                DoubleLog(`🔶${result?.msg}`)
                console.log(result);
            }
        } catch (e) {
            console.log(e);
        }
    }

//查询积分函数
    async points() {
        try {
            const options = {
                //查询积分任务调用签到接口
                url: `https://h5.youzan.com/wscuser/membercenter/stats.json`,
                //请求头, 所有接口通用
                headers: this.headers,
            };
            let result = await httpRequest(options,'get');
            if (result?.code == 0) {
                //obj.error是0代表查询成功
                DoubleLog(`🔷积分余额：${result?.data.stats.points}\n`);
            } else {
	            Notify = 1
                DoubleLog(`🔶${result?.msg}`)
                console.log(result);
            }
        } catch (e) {
            console.log(e);
        }
    }

}


//获取Cookie
async function getCookie() {
    if ($request && $request.method != 'OPTIONS') {
        const bodyValue = $request.body;
        const tokenValue = $request.headers['Qm-User-Token'] || $.request.headers['qm-user-token'] || $.request.headers['QM-USER-TOKEN'];
        if (bodyValue && tokenValue) {
            $.setdata(tokenValue, env_token);
            $.setjson(bodyValue, env_body)
            $.msg($.name, "", "获取签到Cookie成功🎉");
        } else {
            $.msg($.name, "", "错误获取签到Cookie失败");
        }
    }
}

//主程序执行入口
!(async () => {
    //没有设置变量,执行Cookie获取
    if (typeof $request != "undefined") {
        await getCookie();
        return;
    }
    //未检测到ck，退出
    if (!(await checkEnv())) {throw new Error(`❌未检测到ck，请添加环境变量`)};
    if (userList.length > 0) {
        await main();
    }
})()
    .catch((e) => $.notifyMsg.push(e.message || e))//捕获登录函数等抛出的异常, 并把原因添加到全局变量(通知)
    .finally(async () => {
        if ($.barkKey) { //如果已填写Bark Key
            await BarkNotify($, $.barkKey, $.name, $.notifyMsg.join('\n')); //推送Bark通知
        };
        await SendMsg($.notifyMsg.join('\n'))//带上总结推送通知
        $.done(); //调用Surge、QX内部特有的函数, 用于退出脚本执行
    });

/** --------------------------------辅助函数区域------------------------------------------- */

// 双平台log输出
function DoubleLog(data) {
    if ($.isNode()) {
        if (data) {
            console.log(`${data}`);
            $.notifyMsg.push(`${data}`);
        }
    } else {
        console.log(`${data}`);
        $.notifyMsg.push(`${data}`);
    }
}

//把json 转为以 ‘&’ 连接的字符串
function toParams(body) {
    var params = Object.keys(body).map(function (key) {
        return encodeURIComponent(key) + "=" + encodeURIComponent(body[key]);
    }).join("&");
    return params;
}

//检查变量
async function checkEnv() {
    if (userCookie) {
        // console.log(userCookie);
        let e = envSplitor[0];
        for (let o of envSplitor)
            if (userCookie.indexOf(o) > -1) {
                e = o;
                break;
            }
        for (let n of userCookie.split(e)) n && userList.push(new UserInfo(n));
        userCount = userList.length;
    } else {
        console.log("未找到CK");
        return;
    }
    return console.log(`共找到${userCount}个账号`), true;//true == !0
}

/**
 * 随机整数生成
 */
function randomInt(min, max) {
    return Math.round(Math.random() * (max - min) + min);
}
// 发送消息
async function SendMsg(message) {
    if (!message) return;
    if (Notify > 0) {
        if ($.isNode()) {
            await notify.sendNotify($.name, message)
        } else {
            $.msg($.name, '', message)
        }
    } else {
        console.log(message)
    }
}

/** ---------------------------------固定不动区域----------------------------------------- */
// prettier-ignore

